INSERT INTO `report` VALUES (1,NULL,'Active Tickets','SELECT p.value AS __color__,\n   id AS ticket, summary, component, version, milestone, t.type AS type,\n   owner, status,\n   time AS created,\n   changetime AS _changetime, description AS _description,\n   reporter AS _reporter\n  FROM ticket t\n  LEFT JOIN enum p ON p.name = t.priority AND p.type = \'priority\'\n  WHERE status <> \'closed\'\n  ORDER BY CAST(p.value AS signed), milestone, t.type, time\n',' * List all active tickets by priority.\n * Color each row based on priority.\n'),(2,NULL,'Active Tickets by Version','SELECT p.value AS __color__,\n   version AS __group__,\n   id AS ticket, summary, component, version, t.type AS type,\n   owner, status,\n   time AS created,\n   changetime AS _changetime, description AS _description,\n   reporter AS _reporter\n  FROM ticket t\n  LEFT JOIN enum p ON p.name = t.priority AND p.type = \'priority\'\n  WHERE status <> \'closed\'\n  ORDER BY (version IS NULL),version, CAST(p.value AS signed), t.type, time\n','This report shows how to color results by priority,\nwhile grouping results by version.\n\nLast modification time, description and reporter are included as hidden fields\nfor useful RSS export.\n'),(3,NULL,'Active Tickets by Milestone','SELECT p.value AS __color__,\n   concat(\'Milestone \', milestone) AS __group__,\n   id AS ticket, summary, component, version, t.type AS type,\n   owner, status,\n   time AS created,\n   changetime AS _changetime, description AS _description,\n   reporter AS _reporter\n  FROM ticket t\n  LEFT JOIN enum p ON p.name = t.priority AND p.type = \'priority\'\n  WHERE status <> \'closed\'\n  ORDER BY (milestone IS NULL),milestone, CAST(p.value AS signed), t.type, time\n','This report shows how to color results by priority,\nwhile grouping results by milestone.\n\nLast modification time, description and reporter are included as hidden fields\nfor useful RSS export.\n'),(4,NULL,'Accepted, Active Tickets by Owner','SELECT p.value AS __color__,\n   owner AS __group__,\n   id AS ticket, summary, component, milestone, t.type AS type, time AS created,\n   changetime AS _changetime, description AS _description,\n   reporter AS _reporter\n  FROM ticket t\n  LEFT JOIN enum p ON p.name = t.priority AND p.type = \'priority\'\n  WHERE status = \'accepted\'\n  ORDER BY owner, CAST(p.value AS signed), t.type, time\n','List accepted tickets, group by ticket owner, sorted by priority.\n'),(5,NULL,'Accepted, Active Tickets by Owner (Full Description)','SELECT p.value AS __color__,\n   owner AS __group__,\n   id AS ticket, summary, component, milestone, t.type AS type, time AS created,\n   description AS _description_,\n   changetime AS _changetime, reporter AS _reporter\n  FROM ticket t\n  LEFT JOIN enum p ON p.name = t.priority AND p.type = \'priority\'\n  WHERE status = \'accepted\'\n  ORDER BY owner, CAST(p.value AS signed), t.type, time\n','List tickets accepted, group by ticket owner.\nThis report demonstrates the use of full-row display.\n'),(6,NULL,'All Tickets By Milestone  (Including closed)','SELECT p.value AS __color__,\n   t.milestone AS __group__,\n   (CASE status\n      WHEN \'closed\' THEN \'color: #777; background: #ddd; border-color: #ccc;\'\n      ELSE\n        (CASE owner WHEN $USER THEN \'font-weight: bold\' END)\n    END) AS __style__,\n   id AS ticket, summary, component, status,\n   resolution,version, t.type AS type, priority, owner,\n   changetime AS modified,\n   time AS _time,reporter AS _reporter\n  FROM ticket t\n  LEFT JOIN enum p ON p.name = t.priority AND p.type = \'priority\'\n  ORDER BY (milestone IS NULL), milestone DESC, (status = \'closed\'),\n        (CASE status WHEN \'closed\' THEN changetime ELSE (-1) * CAST(p.value AS signed) END) DESC\n','A more complex example to show how to make advanced reports.\n'),(7,NULL,'My Tickets','SELECT p.value AS __color__,\n       (CASE\n         WHEN owner = $USER AND status = \'accepted\' THEN \'Accepted\'\n         WHEN owner = $USER THEN \'Owned\'\n         WHEN reporter = $USER THEN \'Reported\'\n         ELSE \'Commented\' END) AS __group__,\n       t.id AS ticket, summary, component, version, milestone,\n       t.type AS type, priority, t.time AS created,\n       t.changetime AS _changetime, description AS _description,\n       reporter AS _reporter\n  FROM ticket t\n  LEFT JOIN enum p ON p.name = t.priority AND p.type = \'priority\'\n  WHERE t.status <> \'closed\' AND\n        (owner = $USER OR reporter = $USER OR\n         EXISTS (SELECT * FROM ticket_change tc\n                 WHERE tc.ticket = t.id AND tc.author = $USER AND\n                       tc.field = \'comment\'))\n  ORDER BY (COALESCE(owner, \'\') = $USER AND status = \'accepted\') DESC,\n           COALESCE(owner, \'\') = $USER DESC,\n           COALESCE(reporter, \'\') = $USER DESC,\n           CAST(p.value AS signed), milestone, t.type, t.time\n','This report demonstrates the use of the automatically set\nUSER dynamic variable, replaced with the username of the\nlogged in user when executed.\n'),(8,NULL,'Active Tickets, Mine first','SELECT p.value AS __color__,\n   (CASE owner\n     WHEN $USER THEN \'My Tickets\'\n     ELSE \'Active Tickets\'\n    END) AS __group__,\n   id AS ticket, summary, component, version, milestone, t.type AS type,\n   owner, status,\n   time AS created,\n   changetime AS _changetime, description AS _description,\n   reporter AS _reporter\n  FROM ticket t\n  LEFT JOIN enum p ON p.name = t.priority AND p.type = \'priority\'\n  WHERE status <> \'closed\'\n  ORDER BY (COALESCE(owner, \'\') = $USER) DESC, CAST(p.value AS signed), milestone, t.type, time\n',' * List all active tickets by priority.\n * Show all tickets owned by the logged in user in a group first.\n');
/*!40000 ALTER TABLE `report` ENABLE KEYS */;
UNLOCK TABLES;

