INSERT INTO `ticket` VALUES (1,'defect',1629202545930093,1629202545930093,'component1',NULL,'major','somebody','user',NULL,NULL,NULL,'new',NULL,'Ticket 1','ckldjfadjfklajd;skf',NULL),(2,'defect',1629202553480636,1629202553480636,'component1',NULL,'major','somebody','user',NULL,NULL,NULL,'new',NULL,'Ticket 2','fakdsjfakldsjfkl;a',NULL),(3,'defect',1629202560218826,1629202560218826,'component1',NULL,'major','somebody','user',NULL,NULL,NULL,'new',NULL,'Ticket 3','sdaklfjkladsj;fa',NULL),(4,'enhancement',1629202588304822,1629202588304822,'component2',NULL,'minor','somebody','user',NULL,NULL,NULL,'new',NULL,'Ticket 4','fakdsjfakjdsl kfjakd j \r\n soda fad \r\n jlkdsj fkdsjfkla\r\nklfjdslkjflak. add jfkla\r\nafldjflkj ',NULL),(5,'defect',1629203856311639,1629203856311639,'component1',NULL,'major',NULL,'user',NULL,'1.0',NULL,'new',NULL,'Ticket 5','This is a new ticket 5',NULL),(6,'defect',1629294054642160,1629294054642160,'component1',NULL,'major','somebody','user',NULL,NULL,NULL,'new',NULL,'Ticket 6','fsafds\r\nf\r\nasdf\r\nads\r\nfa\r\nsdf\r\n\r\n== fdsfadsf ==\r\n\'\'Italic\'\'\r\n\'\'\'Bold\'\'\'\r\n',NULL),(7,'defect',1629459804044903,1629459857443847,'component1',NULL,'major','user','user',NULL,NULL,NULL,'assigned',NULL,'Ticket 7','fadfadf',NULL),(8,'defect',1629463109113789,1629463109113789,'component1',NULL,'major','somebody','user',NULL,NULL,NULL,'new',NULL,'Ticket 8','fksdjflkadjfk\r\nflkdsjfkdas\r\nflkdsjflkadsfj\r\nfdsjflkdjskfjalkdsjf\r\nfjklsdjflkadjsflkjafl',NULL),(9,'defect',1629463125980736,1629463125980736,'component1',NULL,'major','somebody','user',NULL,NULL,NULL,'new',NULL,'Ticket 9','faksdjflkajd\r\nfladksjflkadjsf\r\nfjalkdsjflkad\r\nfjsadkljflkasd\r\nfjkldsjfla\r\njflksdjfa\r\nfjlkdsjfa\r\nfjldsjfljflkjlkajkylfjdsl fjdskfjlkajflk jdsfjlkadsfjlk asjfd ',NULL),(10,'defect',1629787922983952,1629787922983952,'component1',NULL,'major','somebody','user',NULL,NULL,NULL,'new',NULL,'Ticket 10','\r\n== 10th Ticket ==\r\n\r\nThis is the \'\'\'10th\'\'\' \'\'Ticket\'\'\r\n\r\n* element in the list\r\n  * nested element\r\n    a. more nesting with abc\r\n      1. nesting with 123\r\n\r\n{{{#!ruby\r\n    def foo(a)\r\n        puts a.to_s\r\n    end\r\n\r\n    foo(\"hello world\")\r\n}}}\r\n\r\n----\r\n\r\n>>>> I said something\r\n>>> he replied\r\n>> I replied back\r\n> He replied back',NULL);
